list-view-config.query-select:
    PREFIX foaf: <http://xmlns.com/foaf/0.1/>
    PREFIX ld4l: <http://bib.ld4l.org/ontology/>
    PREFIX prov: <http://www.w3.org/ns/prov#>
    PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
    PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
	PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
	PREFIX bibframe: <http://id.loc.gov/ontologies/bibframe/>

    SELECT DISTINCT 
        ?activity
        ?subclassLabel
        ?work ?workTitle

    WHERE {
        ?subject ?property ?activity .
        ?activity a ld4l:Activity ;
            ld4l:isActivityOf ?work .
        ?work a bibframe:Work ;
            bibframe:title ?title .
        ?title a bibframe:Title ;
            rdfs:label ?workTitle .
        OPTIONAL {
            ?activity vitro:mostSpecificType ?subclass .
            ?subclass rdfs:subClassOf ld4l:Activity; 
                rdfs:label ?subclassLabel .
        }
    }
